class Perceptron :     def __init__(self, a,b, c, tval):        self.x = a         self.result = b         self.cresult = c         self.threshold = tval         self.w = []    def h(self, tw):        hresult= []        for i in range(0 , len(self.result)):            hresult.append(0)            for j in range(0,len(tw)):                hresult[i] = hresult[i] + ( tw[j][i]*self.x[j][i] )         return hresult    def checkthreshold(self, hresult):          actfun =[]        for i in range(0 , len(self.result)) :                        if (hresult[i] <= self.threshold ):                actfun.append(0)            else :                actfun.append( 1)        #print("ans :", hresult)        #print("result of act func:", actfun)        for i in range(0 , len(self.x)) :            if (actfun[i] != self.result[i]) :                return False        return True    def training(self, tw, alpha):         i=1        while i<=100 :                #print("Attempt :", i)                hresult = self.h(tw)                            if(self.checkthreshold(hresult)) :                    self.w = tw                                                           print("In Attempt number ", i,  ", fine tuned " )                    for x in range(0,len(self.w)):                        print("w", x, " --> ", self.w[x])                    break                i = i +1                    for j in range(0,len(self.result)) :                    for k in range(0, len(tw)):                        sum = 0                        for n in range(0, len(tw)):                                 sum = sum + (self.cresult[j] - hresult[j]) *self.x[n][j]                                                tw[k][j] = tw[k][j] + alpha*sum                                if(i>=100):            print("max attempts done")        a = [ [1,1,1,1], [0,0,1,1] , [0,1,0,1] ] b = [0,1,1,1] c = [0.5, 0.7, 1.3, 1.5]  p = Perceptron(a,b,c, 0.5)print("reservation done ?", p.x[0])print(" raining ? ", p.x[1])print("threshold ", p.threshold)r =  p.h([ [0.5,0.5,0.5,0.5], [0.8, 0.8, 0.8, 0.8], [0.2, 0.2, 0.2, 0.2]])print("status", p.checkthreshold(r))print("ex1")p.training( [ [0.7,0.7,0.7,0.7], [0.5, 0.5, 0.5, 0.5], [0.4, 0.4, 0.4, 0.4]], 0.01)print("ex2")p.training( [ [0.7,0.7,0.7,0.7], [0.5, 0.5, 0.5, 0.5], [0.4, 0.4, 0.4, 0.4]], 0.5)print("ex3")p.training( [ [0.2,0.2,0.2,0.2], [0.3, 0.3, 0.3, 0.3], [0.5, 0.5, 0.5, 0.5]], 0.01)